-- ----------------------------
-- Table structure for sys_cinema
-- ----------------------------
DROP TABLE IF EXISTS `sys_cinema`;
CREATE TABLE `sys_cinema`  (
                               `cinema_id` bigint(20) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT 'Cinema ID',
                               `cinema_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT 'Cinema name',
                               `hall_category_list` json NULL COMMENT 'Hall categories owned by the cinema',
                               `cinema_picture` json NULL COMMENT 'Cinema pictures, stored as multiple images in JSON format',
                               `cinema_phone` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT 'Cinema phone number',
                               `cinema_address` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT 'Cinema address',
                               `work_start_time` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT 'Opening time',
                               `work_end_time` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT 'Closing time',
                               PRIMARY KEY (`cinema_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 2 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = DYNAMIC;


-- ----------------------------
-- Table structure for sys_hall
-- ----------------------------
DROP TABLE IF EXISTS `sys_hall`;
CREATE TABLE `sys_hall`  (
                             `hall_id` bigint(20) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT 'Hall ID',
                             `cinema_id` bigint(20) UNSIGNED NOT NULL DEFAULT 1 COMMENT 'Cinema ID',
                             `hall_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT 'Hall name',
                             `hall_category` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT 'Hall category',
                             `row_start` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '1' COMMENT 'Starting row number, such as Row 1/Row A, default is Row 1',
                             `row_nums` smallint(5) UNSIGNED NOT NULL DEFAULT 10 COMMENT 'Total number of rows, default is 10',
                             `seat_nums_row` smallint(5) UNSIGNED NOT NULL DEFAULT 18 COMMENT 'Number of seats per row, default is 18',
                             `seat_nums` smallint(5) UNSIGNED NOT NULL DEFAULT 180 COMMENT 'Total number of available seats, used to determine if the hall is fully booked, default is 180',
                             `seat_state` json NOT NULL COMMENT 'Status of all seats, 0 means available, 1 means disabled, 2 means sold, reflected in the session table',
                             `del_state` tinyint(3) NOT NULL DEFAULT 0 COMMENT 'Deletion flag, 0 for not deleted, 1 for deleted, default is 0',
                             PRIMARY KEY (`hall_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 40 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = DYNAMIC;

