-- Table structure for sys_session
-- ----------------------------
DROP TABLE IF EXISTS `sys_session`;
CREATE TABLE `sys_session`  (
                                `session_id` bigint(20) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT 'Session ID',
                                `hall_id` bigint(20) UNSIGNED NOT NULL COMMENT 'Hall ID',
                                `language_version` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT 'Language version',
                                `movie_id` bigint(20) UNSIGNED NOT NULL COMMENT 'Movie ID',
                                `play_time` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT 'Start time of the session',
                                `end_time` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT 'End time of the session',
                                `deadline` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT 'Deadline for operation, movie and hall information cannot be deleted after this time',
                                `session_date` date NOT NULL COMMENT 'Date of the session',
                                `session_price` decimal(10, 2) NOT NULL COMMENT 'Ticket price',
                                `session_tips` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT 'Hall tips (e.g., mask requirements, benefits displayed next to the seat selection and ticket purchase location, format like: Hall 6 - (Mask required, no eating). Free parking for 3 hours with ticket)',
                                `session_seats` json NOT NULL COMMENT 'Seat information, stored as {key: [], key: []} where key represents the row and the array (index + 1) represents the seat number. Value 0 indicates available, 1 indicates empty (no seat), 2 indicates selected, and 3 indicates sold.',
                                `seat_nums` smallint(5) UNSIGNED NULL DEFAULT 0 COMMENT 'Total number of seats',
                                `sall_nums` smallint(5) NOT NULL DEFAULT 0 COMMENT 'Number of seats sold',
                                `del_state` tinyint(3) NOT NULL DEFAULT 0 COMMENT 'Deletion flag, 0 not deleted, 1 deleted, default 0',
                                PRIMARY KEY (`session_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 59 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = DYNAMIC;

