                             `email` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT 'User email, used for personal information and notifications',
                             `phone_number` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT 'User phone number, used for registration and notifications',
                             `sex` tinyint(3) UNSIGNED NOT NULL COMMENT 'User gender, 1 for male, 0 for female',
                             `user_picture` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT 'User profile picture',
                             `role_id` bigint(20) UNSIGNED NOT NULL DEFAULT 3 COMMENT 'Role ID',
                             `birthday` date NULL DEFAULT NULL COMMENT 'Birthday',
                             `autograph` varchar(1000) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT 'Personal signature',
                             `del_state` tinyint(3) NOT NULL DEFAULT 0 COMMENT 'Deletion flag, 0 for not deleted, 1 for deleted, default 0',
                             PRIMARY KEY (`user_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 30 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = DYNAMIC;

